create database stu_info;
use stu_info;
create table polls_studentinfo(stu_id varchar(20),
	stu_name varchar(20),
    stu_pwd varchar(20),
    stu_email varchar(50),
    stu_course varchar(100),
    stu_role varchar(1));

use stu_info;

select * from polls_studentinfo;

use stu_info;
create table polls_courseinfo(
    course_id varchar(20),                         #id csc3170
	course_name varchar(50),                         #name database design
    course_instructor varchar(20),
    course_description varchar(1000)
);


select * from polls_courseinfo;

use stu_info;

create table polls_studentmark(
	mark_id varchar(20),
	stu_id varchar(20),
	stu_name varchar(20),
    course_id varchar(50),
    assignment_1 varchar(20),
    assignment_2 varchar(20),
    assignment_3 varchar(20),
    assignment_4 varchar(20),
    assignment_5 varchar(20),
    assignment_6 varchar(20));

use stu_info;






select * from polls_studentmark;
